<style>
/* BrainScores Custom Upload Styles - Harmonized with Framer */
:root {
    --background-color: var(--token-5bd97f09-ab9c-40a5-aa80-8ed1d110d3e2, #4b3f3f);
    --surface-color: rgba(0, 0, 0, 0.2); 
    --surface-border: rgba(255, 255, 255, 0.1);
    --accent-color: var(--token-b92d0769-efce-4c68-9078-ab202eac8623, #faea05);
    --text-color: var(--token-28ed214e-3e80-47cf-aa50-ad9244e2e291, #f4f2f1);
    --text-muted: rgba(244, 242, 241, 0.6);
    --error-color: #ff6b6b;
    --success-color: var(--accent-color);
}

body {
    background-color: var(--background-color);
    color: var(--text-color);
    font-family: "Inter", sans-serif;
    min-height: 100vh;
}

/* Hide Framer Content Sections if any remain */
#hero, #context, #approach, #validation, #platform, #team, #integration {
    display: none !important;
}

/* Ensure Nav is visible and on top */
.framer-1kjfpsh-container { 
    z-index: 1000 !important; 
}

/* Content Container Styles */
.custom-upload-container {
    width: 100%;
    max-width: 1000px;
    margin: 140px auto 40px auto;
    padding: 20px;
    z-index: 10;
    position: relative;
    display: flex;
    flex-direction: column;
    align-items: center;
}

h1 {
    font-family: "TASA Explorer", "Inter", sans-serif;
    font-size: 3.5rem;
    font-weight: 500;
    margin-bottom: 20px;
    text-align: center;
    letter-spacing: -0.02em;
    color: var(--text-color);
}

p.subtitle {
    font-family: "Inter", sans-serif;
    color: var(--text-muted);
    margin-bottom: 60px;
    text-align: center;
    max-width: 500px;
    line-height: 1.6;
    font-size: 1.1rem;
}

/* Auth Forms */
.auth-wrapper {
    display: flex;
    gap: 30px;
    flex-wrap: wrap;
    justify-content: center;
    width: 100%;
}

.auth-card {
    background: var(--surface-color);
    border: 1px solid var(--surface-border);
    padding: 40px;
    border-radius: 12px;
    width: 100%;
    max-width: 380px;
    backdrop-filter: blur(10px);
    transition: transform 0.2s ease, border-color 0.2s ease;
}

.auth-card:hover {
    transform: translateY(-2px);
    border-color: rgba(255, 255, 255, 0.2);
}

.auth-card h2 {
    font-family: "TASA Explorer", "Inter", sans-serif;
    margin-top: 0;
    font-size: 1.5rem;
    margin-bottom: 30px;
    font-weight: 500;
    color: var(--text-color);
    text-align: center;
}

a.logo {
    display: flex;
    align-items: center;
    justify-content: center;
    color: var(--text-color);
    text-decoration: none;
    font-family: "TASA Explorer", "Inter", sans-serif;
    font-weight: 700;
    font-size: 1.5rem;
    transition: opacity 0.2s ease;
    margin-bottom: 10px;
}

a.logo:hover {
    opacity: 0.8;
    text-decoration: none;
}

label {
    display: block;
    margin-bottom: 8px;
    font-size: 0.8rem;
    color: var(--text-muted);
    text-transform: uppercase;
    letter-spacing: 0.05em;
    font-weight: 600;
}

input[type="text"],
input[type="email"],
input[type="password"],
input[type="number"],
select {
    width: 100%;
    padding: 12px 16px;
    background: rgba(0, 0, 0, 0.2);
    border: 1px solid var(--surface-border);
    border-radius: 8px;
    color: var(--text-color);
    font-family: "Inter", sans-serif;
    margin-bottom: 24px;
    box-sizing: border-box;
    transition: all 0.2s ease;
    font-size: 1rem;
}

input:focus, select:focus {
    outline: none;
    border-color: var(--accent-color);
    background: rgba(0, 0, 0, 0.4);
}

.btn {
    width: 100%;
    padding: 14px;
    background: var(--accent-color);
    color: #4b3f3f; /* Dark text for contrast */
    border: none;
    border-radius: 50px;
    font-weight: 600;
    font-size: 1rem;
    cursor: pointer;
    transition: transform 0.1s ease, opacity 0.2s ease;
    font-family: "Inter", sans-serif;
}

.btn:hover {
    opacity: 0.9;
    transform: scale(0.99);
}

.btn-outline {
    background: transparent;
    border: 1px solid var(--text-muted);
    color: var(--text-color);
}

.btn-outline:hover {
    border-color: var(--text-color);
    background: rgba(255,255,255,0.05);
}

.upload-card {
    background: var(--surface-color);
    border-radius: 12px;
    padding: 50px;
    width: 100%;
    max-width: 650px;
    text-align: center;
    border: 1px solid var(--surface-border);
    backdrop-filter: blur(10px);
}

.drop-zone {
    border: 1px dashed var(--surface-border);
    border-radius: 8px;
    padding: 60px 20px;
    cursor: pointer;
    transition: all 0.2s;
    position: relative;
    background: rgba(0, 0, 0, 0.1);
    margin-bottom: 30px;
}

.drop-zone:hover, .drop-zone.dragover {
    border-color: var(--accent-color);
    background: rgba(250, 234, 5, 0.05);
    border-style: solid;
}

.drop-zone-icon {
    color: var(--accent-color);
    width: 40px;
    height: 40px;
    margin-bottom: 15px;
}

.progress-bar {
    background: var(--accent-color);
    box-shadow: 0 0 15px rgba(250, 234, 5, 0.3);
    height: 4px;
    width: 0%;
    transition: width 0.3s;
}

.progress-container {
    width: 100%;
    background: rgba(255,255,255,0.1);
    height: 4px;
    border-radius: 2px;
    margin-top: 20px;
    display: none;
    overflow: hidden;
}

.upload-status {
    margin-top: 15px;
    font-size: 0.9rem;
    min-height: 20px;
    color: var(--text-muted);
}

.user-info {
    width: 100%;
    display: flex;
    justify-content: flex-end;
    align-items: center;
    gap: 15px;
    font-size: 0.9rem;
    color: var(--text-muted);
    margin-bottom: 20px;
}

.notice-banner {
    background: rgba(204, 255, 0, 0.05);
    border: 1px solid var(--accent-color);
    color: var(--accent-color);
    padding: 15px;
    border-radius: 8px;
    margin-bottom: 30px;
    font-size: 0.85rem;
    text-align: left;
    line-height: 1.6;
}

.alert-error {
    background: rgba(255, 107, 107, 0.1);
    border: 1px solid var(--error-color);
    color: var(--error-color);
    padding: 15px;
    border-radius: 8px;
    margin-bottom: 20px;
    text-align: center;
}
</style>
</head>
